import React from "react";
import { Table, Input, Popconfirm, Form, Typography, Image, Select } from "antd";
import { EditOutlined, DeleteOutlined } from "@ant-design/icons";
const { Option } = Select;

const EditableCell = ({
  editing,
  dataIndex,
  title,
  inputType,
  record,
  index,
  children,
  ...restProps
}) => {
  const inputNode = inputType;
  return (
    <td {...restProps}>
      {editing ? (
        <Form.Item
          name={dataIndex}
          style={{
            margin: 0,
          }}
          rules={[
            {
              required: true,
              message: `Please Input ${title}!`,
            },
          ]}
        >
          {inputNode}
        </Form.Item>
      ) : (
        children
      )}
    </td>
  );
};

const StudentTable = ({
  students,
  loading,
  deleted,
  editingKey,
  setEditingKey,
  editSave,
  form,
  setDisable,
}) => {
  const { Link } = Typography;

  const isEditing = (record) => record.key === editingKey;

  const edit = (record) => {
    form.setFieldsValue({
      name: "",
      email: "",
      password: "",
      phone: "",
      systemId: "",
      sex: "",
      slipPhoto:"",
      slipVerified:null,
      verified:null, 
      department:'',
      // assignedBus,
      ...record,
    });
    setEditingKey(record.key);
    setDisable(true);
  };

  const cancel = () => {
    setEditingKey("");
    setDisable(false);
  };

  const columns = [
    {
      title: "SystemId",
      dataIndex: "systemId",
      key: "systemId",
      defaultSortOrder: 'descend'
    },
     {
      title: "Name",
      dataIndex: "name",
      key: "name",
      editable: true,
      sorter: (a, b) => a.name.length - b.name.length,

    },
    {
      title: "Email",
      dataIndex: "email",
      key: "email",
      editable: false,
      sorter: (a, b) => a.email.length - b.email.length,

    },
    // {
    //   title: "Password",
    //   dataIndex: "password",
    //   key: "password",
    //   editable: true,
    //   sorter: (a, b) => a.password.length - b.password.length,

    // },
    {
      title: "Phone",
      dataIndex: "phone",
      key: "phone",
      editable: true,
      sorter: (a, b) => a.phone.length - b.phone.length,

    },
    {
      title: "Department",
      dataIndex: "department",
      key: "department",
      sorter: (a, b) => a.department.length - b.department.length,
      render: (_,record) =>{
        return <div>{record. department ? record.department.title :''}</div>
      }
    },
    
    {
      title: "Slip Photo",
      dataIndex: "slipPhoto",
      key: "slipPhoto",
      sorter: (a, b) => a.slipPhoto.length - b.slipPhoto.length,

      render: (_,record) =>{
        return  <Image
        width={200}
        src={record.slipPhoto || "https://www.wkbn.com/wp-content/uploads/sites/48/2020/06/missing-generic.jpg"}
      />
      },

    },
    {
      title: "slipVerified",
      dataIndex: "slipVerified",
      key: "slipVerified",
      sorter: (a, b) => (a === b)? 0 : a? -1 : 1,
      editable: true,
      render: (_,record) =>{
        return <span>{String(record.slipVerified)} </span>
      }
    },
    // {
    //   title: "Account Status",
    //   dataIndex: "verified",
    //   key: "verified",
    //   editable: true,
    //   render: (_,record) =>{
    //     return <span>{String(record.verified)} </span>
    //   }
    // },
    {
      title: "Actions",
      align: "center",
      dataIndex: "operation",
      render: (_, record) => {
        const editable = isEditing(record);
        return editable ? (
          <span>
            <Link
              onClick={() => { 
                editSave(record.key)}
              }
              style={{
                marginRight: 8,
              }}
            >
              Save
            </Link>
            <Popconfirm title="Sure to cancel?" onConfirm={cancel}>
              <Link>Cancel</Link>
            </Popconfirm>
          </span>
        ) : (
          <div>
            <EditOutlined
              style={
                editingKey !== ""
                  ? {
                      display: "none",
                    }
                  : {
                      color: "blue",
                    }
              }
              // editingKey !== ""
              onClick={() => edit(record)}
            >
              Edit
            </EditOutlined>
            <Popconfirm
              title="Are you sureï¼Ÿ"
              okText="Yes"
              // onConfirm={}
              cancelText="No"
              onConfirm={() => deleted(record.key)}
            >
              <DeleteOutlined
                style={
                  editingKey !== ""
                    ? {
                        display: "none",
                      }
                    : {
                        color: "red",
                        marginLeft: "20px",
                      }
                }
                disabled={editingKey !== ""}
              />
            </Popconfirm>
          </div>
        );
      },
    },
  ];


  const mergedColumns = columns.map((col) => {
    if (!col.editable) {
      return col;
    }

    return {
      ...col,
      onCell: (record) => ({
        record,
        inputType:
            col.dataIndex === "slipVerified" ? (
              <Select
                showSearch
                style={{ width: 200 }}
                placeholder="Select Status"
                filterOption={(input, option) =>
                  option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                    <Option key={'slipVerified'} value={'true'}>
                      True
                    </Option>
                    <Option key={'slipVerified2'} value={'false'}>
                      False
                    </Option>
              </Select>
            )   : 
            col.dataIndex === "verified" ? (
              <Select
                style={{ width: 200 }}
                placeholder="Account Status"
                filterOption={(input, option) =>
                  option.children.toLowerCase().indexOf(input.toLowerCase()) >= 0
                }
              >
                    <Option key={'verified1'} value={'true'}>
                      True
                    </Option>
                    <Option key={'verifiedd2'} value={'false'}>
                      False
                    </Option>
              </Select>
            )   : 
            <Input />
            ,
            dataIndex: col.dataIndex,
        title: col.title,
        editing: isEditing(record),
      }),
    };
  });
  return (
    <Form form={form} component={false}>
      <Table
      style={{color:"red"}}
        loading={loading}
        components={{
          body: {
            cell: EditableCell,
          },
        }}
        bordered
        dataSource={students}
        columns={mergedColumns}
        rowClassName="editable-row"
        pagination={{
          onChange: cancel,
        }}
      />
    </Form>
  );
};

export default StudentTable;
